// src/Home.js
import React from "react";
import DateRangePicker from "./ReactDatePicker";
import NumberSelector from "./NumberSelector";

export default function Home() {
  return (
    <div>
      {/* Header */}
      <div className="flex-container header">
        <div>OCBC Travel</div>
      </div>

      {/* Banner image */}
      <div className="img">
        <img
          src={process.env.PUBLIC_URL + "/photos/headliner.jpg"}
          alt="Banner"
        />
      </div>

      {/* Title */}
      <h1 className="centered">Travel Website</h1>
      <p className="centered">Find The Right Destination For You</p>

      {/* Search box */}
      <div className="search-box">
        {/* Destination field */}
        <div className="search-field">
          <label htmlFor="Destination">Destination:</label>
          <input
            type="text"
            id="Destination"
            name="Destination"
            placeholder="Where are you going?"
          />
        </div>

        {/* Stay Period */}
        <div className="search-field">
          <label>Stay Period:</label>
          <DateRangePicker />
        </div>

        {/* Guests */}
        <div className="search-field">
          <label htmlFor="numberOfGuest">No. of Guests:</label>
          <NumberSelector
            id="numberOfGuest"
            label=""
            min={1}
            max={5}
          />
        </div>

        {/* Rooms */}
        <div className="search-field">
          <label htmlFor="numberOfRoom">No. of Rooms:</label>
          <NumberSelector
            id="numberOfRoom"
            label=""
            min={1}
            max={5}
          />
        </div>

        {/* Search button */}
        <div className="search-field">
          <button className="search-btn">Search</button>
        </div>
      </div>
    </div>
  );
}
