import React from "react";
import { useLocation, Link } from "react-router-dom";

const hotels = [
  { name: "Marina Bay Sands", location: "Singapore", price: 500 },
  { name: "Orchard Hotel", location: "Singapore", price: 200 },
  { name: "Bali Beach Resort", location: "Bali", price: 150 },
  { name: "Bangkok Grand", location: "Bangkok", price: 100 },
];

export default function Results() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const destination = params.get("destination")?.toLowerCase() || "";

  const filteredHotels = hotels.filter((hotel) =>
    hotel.location.toLowerCase().includes(destination)
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>Results for "{destination}"</h2>
      <p>Found {filteredHotels.length} hotels.</p>
      {filteredHotels.map((hotel, index) => (
        <div key={index} style={{ margin: "10px 0", border: "1px solid #ccc", padding: "10px" }}>
          <h3>{hotel.name}</h3>
          <p>Location: {hotel.location}</p>
          <p>Price: ${hotel.price}</p>
        </div>
      ))}
      <Link to="/">‚Üê Back to Search</Link>
    </div>
  );
}
